import React, { useMemo, useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useToast } from '../../context/ToastContext';
import { Link } from 'react-router-dom';

const ProfilePage = () => {
  const { user } = useAuth();
  const { showToast } = useToast();

  const [activeTab, setActiveTab] = useState('profile-info');

  // Basic info form state
  const [profileForm, setProfileForm] = useState(() => ({
    fullName: user?.name || '',
    email: user?.email || '',
    phone: '',
    birthday: ''
  }));

  // Addresses state (mock, client-side only)
  const [addresses, setAddresses] = useState([]);
  const [addressForm, setAddressForm] = useState({
    fullName: user?.name || '',
    phone: '',
    addressLine: '',
    city: '',
    district: '',
    ward: '',
    isDefault: false
  });

  // Change password form state (mock)
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const isLoggedIn = useMemo(() => Boolean(user), [user]);

  const handleTabClick = (tabId) => {
    setActiveTab(tabId);
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    showToast('Cập nhật thông tin cá nhân thành công!', 'success');
  };

  const handleAddressChange = (e) => {
    const { name, value, type, checked } = e.target;
    setAddressForm((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleAddAddress = (e) => {
    e.preventDefault();
    const newAddress = {
      id: Date.now(),
      ...addressForm
    };

    setAddresses((prev) => {
      let updated = prev;
      if (newAddress.isDefault) {
        updated = prev.map((a) => ({ ...a, isDefault: false }));
      }
      return [newAddress, ...updated];
    });

    setAddressForm({
      fullName: user?.name || '',
      phone: '0123454655',
      addressLine: 'P813',
      city: 'Hà Nội',
      district: 'Cầu Giấy',
      ward: 'Phường Trung Hòa',
      isDefault: false
    });

    showToast('Đã thêm địa chỉ mới!', 'success');
  };

  const handleDeleteAddress = (id) => {
    setAddresses((prev) => prev.filter((a) => a.id !== id));
    showToast('Đã xóa địa chỉ.', 'info');
  };

  const handleMakeDefault = (id) => {
    setAddresses((prev) => prev.map((a) => ({ ...a, isDefault: a.id === id })));
    showToast('Đã đặt địa chỉ mặc định.', 'success');
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordForm((prev) => ({ ...prev, [name]: value }));
  };

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    if (!passwordForm.currentPassword || !passwordForm.newPassword) {
      showToast('Vui lòng nhập đầy đủ thông tin.', 'warning');
      return;
    }
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      showToast('Xác nhận mật khẩu không khớp.', 'danger');
      return;
    }
    showToast('Đổi mật khẩu thành công!', 'success');
    setPasswordForm({ currentPassword: '', newPassword: '', confirmPassword: '' });
  };

  if (!isLoggedIn) {
    return (
      <div className="container page-content">
        <div className="alert alert-warning d-flex justify-content-between align-items-center">
          <div>Vui lòng đăng nhập để xem trang hồ sơ cá nhân.</div>
          <Link to="/login" className="btn btn-primary btn-sm">Đăng nhập</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container page-content">
      <h1 className="mb-4">Thông tin cá nhân</h1>
      <div className="row">
        <div className="col-lg-3">
          <div className="nav flex-column nav-pills">
            <button className={`nav-link ${activeTab === 'profile-info' ? 'active' : ''}`} onClick={() => handleTabClick('profile-info')}>Thông tin cơ bản</button>
            <button className={`nav-link ${activeTab === 'addresses' ? 'active' : ''}`} onClick={() => handleTabClick('addresses')}>Địa chỉ giao hàng</button>
            <button className={`nav-link ${activeTab === 'change-password' ? 'active' : ''}`} onClick={() => handleTabClick('change-password')}>Đổi mật khẩu</button>
          </div>
        </div>
        <div className="col-lg-9">
          <div className="tab-content">
            {activeTab === 'profile-info' && (
              <div className="tab-pane fade show active" id="profile-info">
                <div className="card">
                  <div className="card-body">
                    <h5>Thông tin cơ bản</h5>
                    <form onSubmit={handleProfileSubmit}>
                      <div className="row">
                        <div className="col-md-6 mb-3">
                          <label className="form-label">Họ và tên</label>
                          <input type="text" className="form-control" name="fullName" value={profileForm.fullName} onChange={handleProfileChange} />
                        </div>
                        <div className="col-md-6 mb-3">
                          <label className="form-label">Email</label>
                          <input type="email" className="form-control" name="email" value={profileForm.email} disabled />
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-md-6 mb-3">
                          <label className="form-label">Số điện thoại</label>
                          <input type="tel" className="form-control" name="phone" value={profileForm.phone} onChange={handleProfileChange} />
                        </div>
                        <div className="col-md-6 mb-3">
                          <label className="form-label">Ngày sinh</label>
                          <input type="date" className="form-control" name="birthday" value={profileForm.birthday} onChange={handleProfileChange} />
                        </div>
                      </div>
                      <button type="submit" className="btn btn-primary">Cập nhật</button>
                    </form>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'addresses' && (
              <div className="tab-pane fade show active" id="addresses">
                <div className="card">
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h5>Địa chỉ giao hàng</h5>
                    </div>

                    <form onSubmit={handleAddAddress} className="mb-4">
                      <div className="row g-3">
                        <div className="col-md-6">
                          <label className="form-label">Họ và tên</label>
                          <input className="form-control" name="fullName" value={addressForm.fullName} onChange={handleAddressChange} />
                        </div>
                        <div className="col-md-6">
                          <label className="form-label">Số điện thoại</label>
                          <input className="form-control" name="phone" value={addressForm.phone} onChange={handleAddressChange} />
                        </div>
                        <div className="col-12">
                          <label className="form-label">Địa chỉ</label>
                          <input className="form-control" name="addressLine" value={addressForm.addressLine} onChange={handleAddressChange} />
                        </div>
                        <div className="col-md-4">
                          <label className="form-label">Tỉnh/Thành</label>
                          <input className="form-control" name="city" value={addressForm.city} onChange={handleAddressChange} />
                        </div>
                        <div className="col-md-4">
                          <label className="form-label">Quận/Huyện</label>
                          <input className="form-control" name="district" value={addressForm.district} onChange={handleAddressChange} />
                        </div>
                        <div className="col-md-4">
                          <label className="form-label">Phường/Xã</label>
                          <input className="form-control" name="ward" value={addressForm.ward} onChange={handleAddressChange} />
                        </div>
                        <div className="col-12 form-check mt-2">
                          <input className="form-check-input" type="checkbox" id="isDefault" name="isDefault" checked={addressForm.isDefault} onChange={handleAddressChange} />
                          <label htmlFor="isDefault" className="form-check-label">Đặt làm địa chỉ mặc định</label>
                        </div>
                        <div className="col-12">
                          <button type="submit" className="btn btn-primary">Thêm địa chỉ</button>
                        </div>
                      </div>
                    </form>

                    <div id="addressesList">
                      {addresses.length === 0 && (
                        <div className="text-muted">Chưa có địa chỉ nào.</div>
                      )}
                      {addresses.map((addr) => (
                        <div key={addr.id} className="border rounded p-3 mb-3">
                          <div className="d-flex justify-content-between">
                            <div>
                              <div className="fw-semibold">{addr.fullName} {addr.isDefault && <span className="badge bg-success ms-2">Mặc định</span>}</div>
                              <div className="text-muted small">{addr.phone}</div>
                              <div className="mt-2">{addr.addressLine}</div>
                              <div className="text-muted">{addr.ward}, {addr.district}, {addr.city}</div>
                            </div>
                            <div className="text-nowrap">
                              {!addr.isDefault && (
                                <button className="btn btn-outline-primary btn-sm me-2" onClick={() => handleMakeDefault(addr.id)}>Đặt mặc định</button>
                              )}
                              <button className="btn btn-outline-danger btn-sm" onClick={() => handleDeleteAddress(addr.id)}>Xóa</button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'change-password' && (
              <div className="tab-pane fade show active" id="change-password">
                <div className="card">
                  <div className="card-body">
                    <h5>Đổi mật khẩu</h5>
                    <form onSubmit={handlePasswordSubmit}>
                      <div className="mb-3">
                        <label className="form-label">Mật khẩu hiện tại</label>
                        <input type="password" className="form-control" name="currentPassword" value={passwordForm.currentPassword} onChange={handlePasswordChange} />
                      </div>
                      <div className="mb-3">
                        <label className="form-label">Mật khẩu mới</label>
                        <input type="password" className="form-control" name="newPassword" value={passwordForm.newPassword} onChange={handlePasswordChange} />
                      </div>
                      <div className="mb-3">
                        <label className="form-label">Xác nhận mật khẩu mới</label>
                        <input type="password" className="form-control" name="confirmPassword" value={passwordForm.confirmPassword} onChange={handlePasswordChange} />
                      </div>
                      <button type="submit" className="btn btn-primary">Đổi mật khẩu</button>
                    </form>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
