import React, { useState } from 'react';

const categories = [
  { value: '', label: 'Tất cả danh mục' },
  { value: 'food', label: 'Thức ăn' },
  { value: 'toys', label: 'Đồ chơi' },
  { value: 'grooming', label: 'Chăm sóc' },
  { value: 'accessories', label: 'Phụ kiện' },
];

const sortOptions = [
  { value: 'price-asc', label: 'Sắp xếp theo giá: Thấp đến cao' },
  { value: 'price-desc', label: 'Sắp xếp theo giá: Cao đến thấp' },
  { value: 'newest', label: 'Sản phẩm mới nhất' },
  { value: 'bestseller', label: 'Bán chạy nhất' },
];

// Dummy data for products
const dummyProducts = Array.from({ length: 12 }, (_, i) => ({
  id: i + 1,
  name: `Sản phẩm ${i + 1}`,
  price: 100000 + i * 50000,
  image: 'https://via.placeholder.com/200x200?text=Product',
  category: categories[(i % (categories.length - 1)) + 1].value,
}));

function ProductsPage() {
  const [category, setCategory] = useState('');
  const [priceFrom, setPriceFrom] = useState('');
  const [priceTo, setPriceTo] = useState('');
  const [sort, setSort] = useState(sortOptions[0].value);

  // Filter and sort logic
  const filteredProducts = dummyProducts
    .filter(p => !category || p.category === category)
    .filter(p => !priceFrom || p.price >= Number(priceFrom))
    .filter(p => !priceTo || p.price <= Number(priceTo))
    .sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      return 0;
    });

  return (
    <div className="page" id="products">
      <div className="container page-content">
        <h1 className="mb-4">Sản phẩm</h1>
        <p className="text-muted mb-4">Tìm sản phẩm hoàn hảo cho thú cưng yêu quý của bạn</p>
        <div className="row">
          <div className="col-lg-3 mb-4">
            <div className="sidebar">
              <h5>Lọc sản phẩm</h5>
              <div className="mb-3">
                <label className="form-label">Danh mục</label>
                <select
                  className="form-select"
                  value={category}
                  onChange={e => setCategory(e.target.value)}
                >
                  {categories.map(c => (
                    <option key={c.value} value={c.value}>{c.label}</option>
                  ))}
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Khoảng giá</label>
                <div className="row">
                  <div className="col-6">
                    <input
                      type="number"
                      className="form-control"
                      placeholder="Từ"
                      value={priceFrom}
                      onChange={e => setPriceFrom(e.target.value)}
                    />
                  </div>
                  <div className="col-6">
                    <input
                      type="number"
                      className="form-control"
                      placeholder="Đến"
                      value={priceTo}
                      onChange={e => setPriceTo(e.target.value)}
                    />
                  </div>
                </div>
              </div>
              {/* Nút lọc không cần thiết vì lọc realtime */}
            </div>
          </div>
          <div className="col-lg-9">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <span className="text-muted" id="productCount">
                Hiển thị {filteredProducts.length} sản phẩm
              </span>
              <select
                className="form-select"
                style={{ width: 'auto' }}
                value={sort}
                onChange={e => setSort(e.target.value)}
              >
                {sortOptions.map(opt => (
                  <option key={opt.value} value={opt.value}>{opt.label}</option>
                ))}
              </select>
            </div>
            <div className="row" id="productsList">
              {filteredProducts.map(product => (
                <div className="col-md-4 mb-4" key={product.id}>
                  <div className="card h-100">
                    <img src={product.image} className="card-img-top" alt={product.name} />
                    <div className="card-body">
                      <h5 className="card-title">{product.name}</h5>
                      <p className="card-text text-primary">{product.price.toLocaleString()}₫</p>
                      <button className="btn btn-outline-primary w-100">Thêm vào giỏ</button>
                    </div>
                  </div>
                </div>
              ))}
              {filteredProducts.length === 0 && (
                <div className="col-12 text-center text-muted py-5">
                  Không tìm thấy sản phẩm phù hợp.
                </div>
              )}
            </div>
            {/* Phân trang có thể bổ sung sau */}
            <nav className="mt-4">
              <ul className="pagination justify-content-center" id="pagination">
                {/* Pagination sẽ bổ sung sau */}
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductsPage;
