import React, { useState, useMemo } from 'react';
import { products } from '../data/products';
import ProductCard from '../components/product/ProductCard';

const ProductsPage = () => {
  const [filters, setFilters] = useState({
    category: '',
    priceFrom: '',
    priceTo: ''
  });
  const [sortBy, setSortBy] = useState('price-low');
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 9;

  // Filter and sort products
  const filteredAndSortedProducts = useMemo(() => {
    let filtered = products.filter(product => {
      // Category filter
      if (filters.category && product.category !== filters.category) {
        return false;
      }
      
      // Price range filter
      if (filters.priceFrom && product.price < parseFloat(filters.priceFrom)) {
        return false;
      }
      if (filters.priceTo && product.price > parseFloat(filters.priceTo)) {
        return false;
      }
      
      return true;
    });

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'newest':
          return b.id - a.id;
        case 'popular':
          return b.rating - a.rating;
        default:
          return 0;
      }
    });

    return filtered;
  }, [filters, sortBy]);

  // Pagination
  const totalPages = Math.ceil(filteredAndSortedProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;
  const currentProducts = filteredAndSortedProducts.slice(startIndex, endIndex);

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
    setCurrentPage(1);
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const renderPagination = () => {
    const pages = [];
    const maxVisiblePages = 5;
    let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
    let endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);

    if (endPage - startPage + 1 < maxVisiblePages) {
      startPage = Math.max(1, endPage - maxVisiblePages + 1);
    }

    // Previous button
    pages.push(
      <li key="prev" className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
        <button 
          className="page-link" 
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Trước
        </button>
      </li>
    );

    // Page numbers
    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <li key={i} className={`page-item ${currentPage === i ? 'active' : ''}`}>
          <button 
            className="page-link" 
            onClick={() => handlePageChange(i)}
          >
            {i}
          </button>
        </li>
      );
    }

    // Next button
    pages.push(
      <li key="next" className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
        <button 
          className="page-link" 
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Sau
        </button>
      </li>
    );

    return pages;
  };

  return (
    <div className="page" id="products">
      <div className="container page-content">
        <h1 className="mb-4">Sản phẩm</h1>
        <p className="text-muted mb-4">Tìm sản phẩm hoàn hảo cho thú cưng yêu quý của bạn</p>
        
        <div className="row">
          <div className="col-lg-3 mb-4">
            <div className="sidebar">
              <h5>Lọc sản phẩm</h5>
              <div className="mb-3">
                <label className="form-label">Danh mục</label>
                <select 
                  className="form-select" 
                  value={filters.category}
                  onChange={(e) => handleFilterChange('category', e.target.value)}
                >
                  <option value="">Tất cả danh mục</option>
                  <option value="food">Thức ăn</option>
                  <option value="toys">Đồ chơi</option>
                  <option value="grooming">Chăm sóc</option>
                  <option value="accessories">Phụ kiện</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Khoảng giá</label>
                <div className="row">
                  <div className="col-6">
                    <input 
                      type="number" 
                      className="form-control" 
                      placeholder="Từ" 
                      value={filters.priceFrom}
                      onChange={(e) => handleFilterChange('priceFrom', e.target.value)}
                      min="0"
                      step="0.01"
                    />
                  </div>
                  <div className="col-6">
                    <input 
                      type="number" 
                      className="form-control" 
                      placeholder="Đến" 
                      value={filters.priceTo}
                      onChange={(e) => handleFilterChange('priceTo', e.target.value)}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>
              </div>
              <button 
                className="btn btn-primary w-100"
                onClick={() => setCurrentPage(1)}
              >
                Lọc
              </button>
            </div>
          </div>
          <div className="col-lg-9">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <span className="text-muted" id="productCount">
                Hiển thị {filteredAndSortedProducts.length} sản phẩm
              </span>
              <select 
                className="form-select" 
                style={{width: 'auto'}}
                value={sortBy}
                onChange={handleSortChange}
              >
                <option value="price-low">Sắp xếp theo giá: Thấp đến cao</option>
                <option value="price-high">Sắp xếp theo giá: Cao đến thấp</option>
                <option value="newest">Sản phẩm mới nhất</option>
                <option value="popular">Bán chạy nhất</option>
              </select>
            </div>
            <div className="row" id="productsList">
              {currentProducts.length > 0 ? (
                currentProducts.map(product => (
                  <ProductCard key={product.id} product={product} />
                ))
              ) : (
                <div className="col-12">
                  <div className="text-center py-5">
                    <h4>Không tìm thấy sản phẩm nào</h4>
                    <p className="text-muted">Hãy thử thay đổi bộ lọc của bạn</p>
                  </div>
                </div>
              )}
            </div>
            <nav className="mt-4">
              <ul className="pagination justify-content-center" id="pagination">
                {totalPages > 1 && renderPagination()}
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductsPage;
